document.addEventListener("DOMContentLoaded",()=>{const e=document.body.querySelectorAll(".nov-swiper");e.length&&e.forEach(e=>{var r=e.querySelector(".swiper-progress"),t=e.parentElement.querySelector(".swiper-prev"),n=e.parentElement.querySelector(".swiper-next");new Swiper(e,{slidesPerView:2,spaceBetween:10,breakpoints:{992:{slidesPerView:3},1200:{spaceBetween:20},1400:{slidesPerView:4}},pagination:{el:r,type:"progressbar"},navigation:{prevEl:t,nextEl:n}})})});